{"changed":true,"filter":false,"title":"user_test.rb","tooltip":"/test/models/user_test.rb","value":"require 'test_helper'\n\nclass UserTest < ActiveSupport::TestCase\n  # test \"the truth\" do\n  #   assert true\n  # end\n  def setup\n      @user = User.new(name: \"Example User\", email: \"user@example.com\",\n                      password: \"foobar\", password_confirmation: \"foobar\")\n  end\n  \n  test \"should be valid\" do\n    assert @user.valid?\n  end\n  \n  test \"name should be present\" do\n    @user.name = \"     \"\n    assert_not @user.valid?\n  end\n  \n  test \"email should be present\" do\n    @user.email = \"     \"\n    assert_not @user.valid?\n  end\n  \n  test \"name should not be too long\" do\n    @user.name = \"a\" * 51\n    assert_not @user.valid?\n  end\n  \n  test \"email should not be too long\" do\n    @user.email = \"a\" * 256\n    assert_not @user.valid?\n  end\n  \n  test \"email validation should accept valid addresses\" do\n    valid_addresses = %w[user@example.com USER@foo.COM A_US-ER@foo.bar.org\n                         first.last@foo.jp alice+bob@baz.cn]\n    valid_addresses.each do |valid_address|\n      @user.email = valid_address\n      assert @user.valid?, \"#{valid_address.inspect} should be valid\"\n    end\n  end\n  \n  test \"email validation should reject invalid addresses\" do\n    invalid_addresses = %w[user@example,com user_at_foo.org user.name@example.\n                           foo@bar_baz.com foo@bar+baz.com]\n    invalid_addresses.each do |invalid_address|\n      @user.email = invalid_address\n      assert_not @user.valid?, \"#{invalid_address.inspect} should be invalid\"\n    end\n  end\n  \n  test \"email addresses should be unique\" do\n    duplicate_user = @user.dup\n    duplicate_user.email = @user.email.upcase\n    @user.save\n    assert_not duplicate_user.valid?\n  end\n  \n  test \"password should have a minimum length\" do\n    @user.password = @user.password_confirmation = \"a\" * 5\n    assert_not @user.valid?\n  end\n  \nend\n","undoManager":{"mark":-580,"position":100,"stack":[[{"group":"doc","deltas":[{"action":"insertText","range":{"start":{"row":54,"column":31},"end":{"row":54,"column":32}},"text":"r"}]}],[{"group":"doc","deltas":[{"action":"insertText","range":{"start":{"row":54,"column":32},"end":{"row":54,"column":33}},"text":"."}]}],[{"group":"doc","deltas":[{"action":"insertText","range":{"start":{"row":54,"column":33},"end":{"row":54,"column":34}},"text":"e"}]}],[{"group":"doc","deltas":[{"action":"insertText","range":{"start":{"row":54,"column":34},"end":{"row":54,"column":35}},"text":"m"}]}],[{"group":"doc","deltas":[{"action":"insertText","range":{"start":{"row":54,"column":35},"end":{"row":54,"column":36}},"text":"a"}]}],[{"group":"doc","deltas":[{"action":"insertText","range":{"start":{"row":54,"column":36},"end":{"row":54,"column":37}},"text":"i"}]}],[{"group":"doc","deltas":[{"action":"insertText","range":{"start":{"row":54,"column":37},"end":{"row":54,"column":38}},"text":"l"}]}],[{"group":"doc","deltas":[{"action":"insertText","range":{"start":{"row":54,"column":38},"end":{"row":54,"column":39}},"text":"."}]}],[{"group":"doc","deltas":[{"action":"insertText","range":{"start":{"row":54,"column":39},"end":{"row":54,"column":40}},"text":"u"}]}],[{"group":"doc","deltas":[{"action":"insertText","range":{"start":{"row":54,"column":40},"end":{"row":54,"column":41}},"text":"p"}]}],[{"group":"doc","deltas":[{"action":"insertText","range":{"start":{"row":54,"column":41},"end":{"row":54,"column":42}},"text":"c"}]}],[{"group":"doc","deltas":[{"action":"insertText","range":{"start":{"row":54,"column":42},"end":{"row":54,"column":43}},"text":"a"}]}],[{"group":"doc","deltas":[{"action":"insertText","range":{"start":{"row":54,"column":43},"end":{"row":54,"column":44}},"text":"s"}]}],[{"group":"doc","deltas":[{"action":"insertText","range":{"start":{"row":54,"column":44},"end":{"row":54,"column":45}},"text":"e"}]}],[{"group":"doc","deltas":[{"action":"inser